package dualbroadcast_test

import (
	"encoding/hex"
	"testing"

	"github.com/ethereum/go-ethereum/common"
	"github.com/ethereum/go-ethereum/common/hexutil"
	"github.com/stretchr/testify/require"

	"github.com/smartcontractkit/chainlink-evm/pkg/txm/clientwrappers/dualbroadcast"
)

func FuzzVerifyResponse(f *testing.F) {
	txData, err := hex.DecodeString("")
	require.NoError(f, err)
	fromAddress := common.HexToAddress("0x7daAe72fc3d948B1fA90502f1b84b6A02CCe7dFd")
	params := "randomData=0x0000000&destination=0x1b4cb47622705f0f67b6b18bbd1aa1a91fc77d37&dapp=0xc38d38333687ea295753c214744e839eddc7aebb"
	fwdrDestAddress := common.HexToAddress("0x8ae79bb7cce2dc3d132c288971b0f74af02a3b4a")
	toAddress := common.HexToAddress("0x1b4cb47622705f0f67b6b18bbd1aa1a91fc77d37")

	var metacallResponse dualbroadcast.MetacalldataResponse
	metacallResponse.ToAddress = toAddress
	var gasLimit hexutil.Big
	require.NoError(f, gasLimit.UnmarshalText([]byte("0x3c97b8")))
	metacallResponse.GasLimit = &gasLimit
	var maxFeePerGas hexutil.Big
	require.NoError(f, maxFeePerGas.UnmarshalText([]byte("0x3b9aca00")))

	validCalldata, err := hexutil.Decode("")
	require.NoError(f, err)
	f.Add(validCalldata)
	invalidCalldata1, err := hexutil.Decode("0x41")
	require.NoError(f, err)
	f.Add(invalidCalldata1)
	invalidCalldata2, err := hexutil.Decode("")
	require.NoError(f, err)
	f.Add(invalidCalldata2)
	invalidCalldata3, err := hexutil.Decode("")
	require.NoError(f, err)
	f.Add(invalidCalldata3)

	f.Fuzz(func(t *testing.T, calldata []byte) {
		metacallResponse.CallData = calldata
		resp, err := dualbroadcast.VerifyResponse(metacallResponse, params, txData, fromAddress, fwdrDestAddress)
		if err == nil && resp != nil {
			require.Equal(t, toAddress, resp.ToAddress)
			require.Equal(t, maxFeePerGas, resp.MaxFeePerGas)
		}
	})
}
